mapper.update:
UPDATE article
SET description = #{description},
    article_id = #{articleId}
WHERE id = #{id}

mapper.select:
SELECT id, article_id AS articleId, description
FROM article
WHERE id = #{id}

mapper.update:
UPDATE article_views
SET views = views + 1
WHERE id = #{articleId}

mapper.select:
SELECT COUNT(*)
FROM article_view
WHERE article_id = #{articleId}

SELECT id, user_id
FROM users
WHERE user_id = #{userId}
