mapper.update:
UPDATE article
SET description = #{description},
    article_id = #{articleId}
WHERE id = #{id}

mapper.select:
SELECT id, article_id AS articleId, description
FROM article
WHERE id = #{id}

mapper.update:
UPDATE article_view
SET views = views + 1
WHERE article_id = #{articleId} AND user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM article_view
WHERE article_id = #{articleId} AND user_id = #{userId}

SELECT views
FROM article_view
WHERE article_id = #{articleId} AND user_id = #{userId}

SELECT id, user_id AS userId
FROM users
WHERE user_id = #{userId}
